public abstract class Command {
	public abstract void execute(String projectId);
}

public abstract class ADTCommand extends Command {
	protected long ADTId;

	public static ADTCommand create (String description, String name, HashMap<String,String> structure, HashMap<String,String> examples, boolean isApiArtifact, boolean isReadOnly) {
		return new Create( description, name, structure, examples, isApiArtifact, isReadOnly );
	}

	public static ADTCommand update(long ADTId, String description, String name, HashMap<String,String> structure) {
		return new Update(ADTId, description, name, structure );
	}

	public static ADTCommand delete(long ADTId) {
		return new Delete(ADTId);
	}

	private ADTCommand(Long ADTId) {
		this.ADTId = ADTId;
		queueCommand(this);
	}

	// All constructors for ADTCommand MUST call queueCommand and the end of
	// the constructor to add the
	// command to the queue.
	private static void queueCommand(Command command) {
		ThreadContext threadContext = ThreadContext.get();
        threadContext.addCommand(command);
	}

	public void execute(final String projectId) {
    	if (ADTId != 0) {
			ADT adt = ADT.find(ADTId);

			if (adt == null)
				System.out
						.println("errore Cannot execute ADTCommand. Could not find ADT for ADTID "
								+ ADTId);
			else {
				execute(adt, projectId);
			}
		} else
			execute(null, projectId);

	}

	public abstract void execute(ADT ADT, String projectId);

	protected static class Create extends ADTCommand {
		private String description;
		private String name;
		private HashMap<String,String> structure;
		private HashMap<String,String> examples;
		private boolean isApiArtifact;
		private boolean isReadOnly;


		public Create(String description, String name, HashMap<String,String> structure,HashMap<String,String> examples, boolean isApiArtifact, boolean isReadOnly) {
			super(0L);
			this.description   = description;
			this.name		   = name;
			this.structure	   = structure;
			this.examples	   = examples;
			this.isApiArtifact = isApiArtifact;
			this.isReadOnly    = isReadOnly;

		}

		public void execute(ADT ADT, String projectId) {
			new ADT( description, name, structure, examples, isApiArtifact, isReadOnly , projectId);
		}
	}

	protected static class Update extends ADTCommand {

		private String description;
		private String name;
		private HashMap<String,String> structure;

		public Update( long ADTId, String description, String name, HashMap<String,String> structure ) {
			super(ADTId);
			this.description  = description;
			this.name		  = name;
			this.structure    = structure;
		}

		public void execute(ADT adt, String projectId) {
			adt.update( description, name, structure );
		}
	}

	protected static class Delete extends ADTCommand {
		public Delete(long ADTId) {
			super(ADTId);
		}

		public void execute(ADT adt, String projectId) {
			adt.delete();
		}
	}

}

public abstract class TestCommand extends Command {
	protected long testId;
	protected boolean isSimple;

	public static TestCommand create(TestDTO test, long functionId, boolean isApiArtifact, boolean isReadOnly ) {
		return new Create(test, functionId, isApiArtifact, isReadOnly);
	}

	public static TestCommand update(TestDTO test) {
		return new Update(test);
	}

	public static TestCommand delete(TestDTO test) {
		return new Delete(test);
	}

	private TestCommand(Long testId, boolean isSimple) {
		this.testId = testId;
		this.isSimple = isSimple;
		queueCommand(this);
	}

	// All constructors for simpleTestCommand MUST call queueCommand and the end of
	// the constructor to add the
	// command to the queue.
	private static void queueCommand(Command command) {
		ThreadContext threadContext = ThreadContext.get();
        threadContext.addCommand(command);
	}

	public void execute(final String projectId) {
    	if (testId != 0) {

    		Test test = Test.find(testId);

			if (test == null)
				System.out
						.println("errore Cannot execute test command. Could not find test for id "+testId);
			else {
				execute(test, projectId);
			}
		} else
			execute(null, projectId);

	}

	public abstract void execute(Test simpleTest, String projectId);

	protected static class Create extends TestCommand {
		private TestDTO testDto;
		private long functionId;
		private boolean isApiArtifact;
		private boolean isReadOnly;

		public Create( TestDTO testDto, long functionId, boolean isApiArtifact, boolean isReadOnly) {
			super(0L,testDto.isSimple);
			this.testDto	   = testDto;
			this.functionId	   = functionId;
			this.isApiArtifact = isApiArtifact;
			this.isReadOnly    = isReadOnly;
		}

		public void execute(Test test, String projectId) {
			System.out.println("CREATING "+testDto.description);
			new Test(testDto, functionId, isApiArtifact, isReadOnly, projectId);
		}
	}

	protected static class Update extends TestCommand {

		private TestDTO testDto;

		public Update( TestDTO testDto ) {
			super(testDto.id,testDto.isSimple);
			this.testDto = testDto;
		}

		public void execute(Test test, String projectId) {
			System.out.println("UPDATING "+test.getId());
			test.update( testDto );
		}
	}

	protected static class Delete extends TestCommand {

		public Delete(TestDTO testDto) {
			super(testDto.id,testDto.isSimple);

		}

		@Override
		public void execute(Test test, String projectId) {
			test.delete();
		}
	}

}

public abstract class MicrotaskCommand extends Command
{
	private Key<Microtask> microtaskKey;

	public static MicrotaskCommand submit(Key<Microtask> microtaskKey, String jsonDTOData, String workerID, int awardedPoint)
		{ return new Submit(microtaskKey, jsonDTOData, workerID, awardedPoint); }

	public static MicrotaskCommand skip(Key<Microtask> microtaskKey, String workerID, boolean disablePoint)
		{ return new Skip(microtaskKey, workerID, disablePoint); }

	public static MicrotaskCommand createReview(Key<Microtask> microtaskKeyToReview, String excludedWorkerID,
			String initiallySubmittedDTO, String workerOfReviewedWork)
		{ return new CreateReview(microtaskKeyToReview, excludedWorkerID, initiallySubmittedDTO, workerOfReviewedWork); }

	public static MicrotaskCommand createChallengeReview(Key<Microtask> reviewKey, String challengeTextDTO)
		{ return new CreateChallengeReview(reviewKey, challengeTextDTO); }

	// Creates a new copy of the specified microtask, reissuing the new microtask with specified
	// worker excluded from performing it and save the reference to the reissued microtask.
	public static MicrotaskCommand reviseMicrotask(Key<Microtask> microtaskKey, String jsonDTOData, String reissueMotivation, String excludedWorkerID, int awardedPoint)
		{ return new ReviseMicrotask(microtaskKey, jsonDTOData, reissueMotivation, excludedWorkerID, awardedPoint); }

	// Creates a new copy of the specified microtask, reissuing the new microtask with specified
	// worker excluded from performing it.
	public static MicrotaskCommand rejectMicrotask(Key<Microtask> microtaskKey, String excludedWorkerID, int awardedPoint)
		{ return new RejectMicrotask(microtaskKey, excludedWorkerID, awardedPoint); }


	private MicrotaskCommand( Key<Microtask> microtaskKey )
	{
		this.microtaskKey = microtaskKey;
		queueCommand(this);
	}

	// All constructors for WorkerCommand MUST call queueCommand by calling the super constructor
	private static void queueCommand(Command command)
	{
		ThreadContext threadContext = ThreadContext.get();
        threadContext.addCommand(command);
	}

	public void execute(final String projectId)
	{

		final Microtask microtask = find(microtaskKey);

	        	if (microtask == null)
	    			System.out.println("ERROR erroreCannot execute MicrotaskCommand. Could not find the microtask for microtaskID "
	    						+ microtaskKey);
	    		else
	    		{
	    			execute(microtask, projectId);
	    		}
	}

	public abstract void execute(Microtask microtask, String projectId);

	// Finds the specified microtask. Returns null if no such microtask exists.
	protected Microtask find(Key<Microtask> microtaskKey)
	{
		return ofy().load().key(microtaskKey).now();
	}


	protected static class Submit extends MicrotaskCommand
	{
		private String jsonDTOData;
		private String workerID;
		private int awardedPoint;


		public Submit(Key<Microtask> microtaskKey, String jsonDTOData, String workerID, int awardedPoint)
		{
			super(microtaskKey);
			this.jsonDTOData = jsonDTOData;
			this.workerID = workerID;
			this.awardedPoint= awardedPoint;
		}

		public void execute(Microtask microtask, String projectId)
		{
			microtask.submit(jsonDTOData, workerID, awardedPoint);
		}
	}

	protected static class Skip extends MicrotaskCommand
	{
		private String workerID;
		private boolean disablePoint;

		public Skip(Key<Microtask> microtaskKey, String workerID, boolean disablePoint)
		{
			super(microtaskKey);
			this.workerID = workerID;
			this.disablePoint=disablePoint;

		}

		public void execute(Microtask microtask, String projectId)
		{
			microtask.skip(workerID, disablePoint, projectId);
		}
	}

	protected static class CreateReview extends MicrotaskCommand
	{
		private Key<Microtask> microtaskKeyToReview;
		private String excludedWorkerID;
		private String initiallySubmittedDTO;
		private String workerOfReviewedWork;

		public CreateReview(Key<Microtask> microtaskKeyToReview, String excludedWorkerID, String initiallySubmittedDTO, String workerOfReviewedWork)
		{
			super(microtaskKeyToReview);
			this.microtaskKeyToReview = microtaskKeyToReview;
			this.excludedWorkerID = excludedWorkerID;
			this.initiallySubmittedDTO = initiallySubmittedDTO;
			this.workerOfReviewedWork = workerOfReviewedWork;
		}

		public void execute(Microtask toReview, String projectId)
		{
				Review review = new Review(microtaskKeyToReview, initiallySubmittedDTO, workerOfReviewedWork, toReview.getFunctionId(), projectId);
				ProjectCommand.queueReviewMicrotask(review.getKey(), excludedWorkerID);
		}
	}


	protected static class CreateChallengeReview extends MicrotaskCommand
	{
		private String challengeTextDTO;
		public CreateChallengeReview(Key<Microtask> reviewKey, String challengeTextDTO)
		{
			super(reviewKey);
			this.challengeTextDTO=challengeTextDTO;
		}
		public void execute(Microtask review, String projectId)
		{
			ChallengeDTO dto=null;
			try {
				dto = (ChallengeDTO)(DTO.read(challengeTextDTO, ChallengeDTO.class));

			} catch( JsonParseException e) {
				e.printStackTrace();
			} catch( JsonMappingException e) {
				e.printStackTrace();
			} catch (IOException e) {
				e.printStackTrace();
			}
			String reviewerWorkerId = review.getWorkerId();
			String challengerWorkerId = ((Review)review).getWorkerOfReviewedWork();
			Key<Microtask> microtaskUnderChallengeKey= ((Review)review).getMicrotaskKeyUnderReview();
			long functionId = review.getFunctionId();
			ChallengeReview challengeReview = new ChallengeReview(dto.challengeText, challengerWorkerId, reviewerWorkerId, microtaskUnderChallengeKey, review.getKey(), functionId, projectId);
			ProjectCommand.queueChallengeReviewMicrotask(challengeReview.getKey(), reviewerWorkerId, challengerWorkerId);

		}

	}

	protected static class RejectMicrotask extends MicrotaskCommand
	{
		private String excludedWorkerID;
		private int awardedPoint;

		public RejectMicrotask(Key<Microtask> microtaskKey, String excludedWorkerID, int awardedPoint)
		{
			super(microtaskKey);
			this.excludedWorkerID = excludedWorkerID;
			this.awardedPoint = awardedPoint;

		}

		// Overrides the default execute as no microtask is to be loaded.
		public void execute(Microtask microtask, String projectId)
		{
			Microtask newMicrotask = microtask.copy(projectId);

			WorkerCommand.awardPoints( excludedWorkerID ,awardedPoint );

			ProjectCommand.queueMicrotask(newMicrotask.getKey(), excludedWorkerID);
		}
	}
	protected static class ReviseMicrotask extends MicrotaskCommand
	{
		private String excludedWorkerID;
		private int awardedPoint;
		private String jsonDTOData;
		private String reissueMotivation;

		public ReviseMicrotask(Key<Microtask> microtaskKey, String jsonDTOData, String reissueMotivation, String excludedWorkerID, int awardedPoint)
		{
			super(microtaskKey);
			this.excludedWorkerID = excludedWorkerID;
			this.awardedPoint = awardedPoint;
			this.jsonDTOData = jsonDTOData;
			this.reissueMotivation = reissueMotivation;
		}

		// Overrides the default execute as no microtask is to be loaded.
		public void execute(Microtask microtask, String projectId)
		{
			microtask.revise(jsonDTOData, excludedWorkerID ,awardedPoint,reissueMotivation, projectId);
		}
	}

}

public abstract class ProjectCommand extends Command
{

	/* PUBLIC METHODS */
	public static ProjectCommand reset(String projectId){
		return new Reset(projectId);
	}
	public static ProjectCommand clear(String projectId){
		return new Clear(projectId);
	}
	public static ProjectCommand construct(String projectId){
		return new Construct(projectId);
	}

	public static ProjectCommand enableReviews(boolean reviewsEnabled){
		return new EnableReviews(reviewsEnabled);
	}

	public static ProjectCommand enableTutorials(boolean tutorialsEnabled){
		return new EnableTutorials(tutorialsEnabled);
	}

	public static ProjectCommand queueMicrotask(Key<Microtask> microtaskKey, String excludedWorkerID) {
		return new QueueMicrotask(microtaskKey, excludedWorkerID);
	}

	public static ProjectCommand queueReviewMicrotask(Key<Microtask> microtaskKey, String excludedWorkerID) {
		return new QueueReviewMicrotask(microtaskKey, excludedWorkerID);
	}
	public static ProjectCommand queueChallengeReviewMicrotask(Key<Microtask> microtaskKey, String firstExcludedWorkerID, String secondExcludedWorkerID) {
		return new QueueChallengeReviewMicrotask(microtaskKey, firstExcludedWorkerID, secondExcludedWorkerID);
	}

	public static ProjectCommand skipMicrotask( String microtaskKey, String workerID , Boolean disablePoint) {
		return new SkipMicrotask(microtaskKey, workerID, disablePoint);
	}


	public static ProjectCommand submitMicrotask(String microtaskKey, String jsonDTOData, String workerID){
		return new SubmitMicrotask(microtaskKey, jsonDTOData, workerID);
	}

	public static ProjectCommand logoutWorker(String workerID){
		return new LogoutWorker(workerID);
	}

	public static ProjectCommand logoutInactiveWorkers(){
		return new LogoutInactiveWorkers();
	}


	public static ProjectCommand notifyLoggedInWorkers( NotificationInFirebase notification ) {
		return new NotifyLoggedInWorkers( notification );
	}


	/* PROTECTED METHODS */

	// create and queue the command
	private ProjectCommand(){
		queueCommand(this);
	}

	public void execute(final String  projectId)
	{
		Project project = Project.Create(projectId);
	    execute(project);
	}

	public abstract void execute(Project project);

	// all commands MUST call queueCommand
	private static void queueCommand(Command command){
		//ThreadContext threadContext = ThreadContext.get();

       // System.out.println("---->"+threadContext.getUserId());
		ThreadContext threadContext = ThreadContext.get();
        threadContext.addCommand(command);
		//CommandContext.ctx.addCommand(command);
	}
	//reset the project
	protected static class Reset extends ProjectCommand
	{
		private String projectID;
		public Reset(String projectID)
		{
			super();
			this.projectID = projectID;
		}

		public void execute(Project project)
		{
			ProjectCommand.clear(projectID);
			ProjectCommand.construct(projectID);

		}
	}
	//reset the project
	protected static class Clear extends ProjectCommand
	{
		private String projectID;
		public Clear(String projectID)
		{
			super();
			this.projectID = projectID;
		}

		public void execute(Project project)
		{
			Project.Clear(projectID);
		}
	}
	//reset the project
	protected static class Construct extends ProjectCommand
	{
		private String projectID;
		public Construct(String projectID)
		{
			super();
			this.projectID = projectID;
		}

		public void execute(Project project)
		{
			Project.Construct(projectID);
		}
	}

	// enable the reviews
	protected static class EnableReviews extends ProjectCommand
	{
		private boolean reviewsEnabled;

		public EnableReviews(boolean reviewsEnabled)
		{
			super();
			this.reviewsEnabled = reviewsEnabled;
		}

		public void execute(Project project)
		{
			project.enableReviews(reviewsEnabled);
		}
	}

	// enable the reviews
	protected static class EnableTutorials extends ProjectCommand
	{
		private boolean tutorialsEnabled;

		public EnableTutorials(boolean tutorialsEnabled)
		{
			super();
			this.tutorialsEnabled = tutorialsEnabled;
		}

		public void execute(Project project)
		{
			project.enableTutorials(tutorialsEnabled);
		}
	}

	// queue a microtask in the project
	protected static class QueueMicrotask extends ProjectCommand
	{
		private Key<Microtask> microtaskKey;
		private String excludedWorkerID;

		public QueueMicrotask(Key<Microtask> microtaskKey, String excludedWorkerID)
		{
			super();
			this.microtaskKey = microtaskKey;
			this.excludedWorkerID = excludedWorkerID;
		}

		public void execute(Project project)
		{
			project.queueMicrotask(microtaskKey, excludedWorkerID);
		}
	}
	// queue a review microtask in the project
	protected static class QueueReviewMicrotask extends ProjectCommand
	{
		private Key<Microtask> microtaskKey;
		private String excludedWorkerID;

		public QueueReviewMicrotask( Key<Microtask> microtaskKey, String excludedWorkerID)
		{
			super();
			this.microtaskKey = microtaskKey;
			this.excludedWorkerID = excludedWorkerID;
		}

		public void execute(Project project)
		{
			project.queueReviewMicrotask(microtaskKey, excludedWorkerID);
		}
	}

	// queue a challenge review microtask in the project
	protected static class QueueChallengeReviewMicrotask extends ProjectCommand
	{
		private Key<Microtask> microtaskKey;
		private String firstExcludedWorkerID;
		private String secondExcludedWorkerID;


		public QueueChallengeReviewMicrotask( Key<Microtask> microtaskKey, String firstExcludedWorkerID, String secondExcludedWorkerID)
		{
			super();
			this.microtaskKey = microtaskKey;
			this.firstExcludedWorkerID = firstExcludedWorkerID;
			this.secondExcludedWorkerID = secondExcludedWorkerID;

		}

		public void execute(Project project)
		{
			project.queueChellengeReviewMicrotask(microtaskKey, firstExcludedWorkerID, secondExcludedWorkerID);
		}
	}

	// skip a microtask
	protected static class SkipMicrotask extends ProjectCommand
	{
		private Key<Microtask> microtaskKey;
		private String workerID;
		private Boolean disablePoint;

		public SkipMicrotask(String microtaskKey, String workerID, Boolean disablePoint)
		{
			super();
			this.microtaskKey = Microtask.stringToKey(microtaskKey) ;
			this.workerID = workerID;
			this.disablePoint = disablePoint;
		}

		public void execute(Project project)
		{
			project.skipMicrotask(microtaskKey, workerID, disablePoint);
		}
	}

	// submit a microtask
	protected static class SubmitMicrotask extends ProjectCommand
	{
		private Key<Microtask> microtaskKey;
		private String jsonDTOData;
		private String workerID;

		public SubmitMicrotask(String microtaskKey, String jsonDTOData, String workerID)
		{
			super();
			this.microtaskKey = Microtask.stringToKey( microtaskKey );
			this.jsonDTOData = jsonDTOData;
			this.workerID = workerID;
		}

		public void execute(Project project)
		{
			project.submitMicrotask(microtaskKey, jsonDTOData, workerID, project);
		}
	}

	// logout a worker
	protected static class LogoutWorker extends ProjectCommand
	{
		private String workerID;

		public LogoutWorker(String workerID)
		{
			super();
			this.workerID = workerID;
		}

		public void execute(Project project)
		{
			project.logoutWorker(workerID);
		}
	}

	// logout all inactive workers
	protected static class LogoutInactiveWorkers extends ProjectCommand
	{
		public LogoutInactiveWorkers()
		{
			super();
		}

		public void execute(Project project)
		{
			project.logoutInactiveWorkers();
		}
	}

	// logout all inactive workers
	protected static class NotifyLoggedInWorkers extends ProjectCommand
	{
		NotificationInFirebase notification;
		public NotifyLoggedInWorkers(NotificationInFirebase notification)
		{
			super();
			this.notification = notification;
		}

		public void execute(Project project)
		{
			HashSet<String> workersId = project.getLoggedInWorkers();
			for( String workerId:workersId ){
				FirebaseService.writeWorkerNotification(notification, workerId, project.getID());
			}
		}
	}
}

public abstract class FunctionCommand extends Command {
	protected long functionId;

	public static FunctionCommand addClientRequestsArtifacts(FunctionDTO functionDTO) {
		return new AddClientRequestsArtifacts(functionDTO);
	}

	public static FunctionCommand create(FunctionDTO functionDTO, boolean isApiArtifact, boolean isReadOnly) {
		return new Create(functionDTO, isApiArtifact, isReadOnly);
	}

	public static FunctionCommand createRequestedFunction(long requestingFunctionId, FunctionDTO requestedFunction) {
		return new CreateRequestedFunction( requestingFunctionId, requestedFunction);
	}

	public static FunctionCommand removeCaller(long functionId,	long callerFunctionID) {
		return new RemoveCaller(functionId, callerFunctionID);
	}

	public static FunctionCommand addCaller(long functionId,
			long callerFunctionID) {
		return new AddCaller(functionId, callerFunctionID);
	}

	public static FunctionCommand addCallee(long functionId,
			long calleeFunctionId) {
		return new AddCaller(functionId, calleeFunctionId);
	}

	public static FunctionCommand runTests(long functionId) {
		return new RunTests(functionId);
	}

	public static FunctionCommand submittedTestResult(long functionId, String jsonDTO){
		return new SubmittedTestResult(functionId, jsonDTO);
	}


	public static FunctionCommand calleeChangedInterface(long functionId,
			long calleeId, int oldCalleeVersion) {
		return new CalleeChangedInterface(functionId, calleeId,
				oldCalleeVersion);
	}

	public static FunctionCommand calleeBecomeDeactivated(long functionId,
			long calleeId, String disputeText) {
		return new CalleeBecomeDeactivated(functionId, calleeId, disputeText);
	}

	public static FunctionCommand addTest(long functionId, long testId) {
		return new AddTest(functionId, testId);
	}

	public static FunctionCommand incrementTestSuite(long functionId) {
		return new IncrementTestSuite(functionId);
	}


	public static FunctionCommand lookForWork(long functionId) {
		return new LookForWork(functionId);
	}

	private FunctionCommand(Long functionId) {
		this.functionId = functionId;
		queueCommand(this);
	}

	// All constructors for FunctionCommand MUST call queueCommand by calling
	// the super constructor
	private static void queueCommand(Command command) {
		ThreadContext threadContext = ThreadContext.get();
        threadContext.addCommand(command);
	}


	public void execute(final String projectId) {
    	if (functionId != 0) {
			Function function = Function.find(functionId);
			if (function == null)
				System.out
						.println("Error Cannot execute FunctionCommand. Could not find the function for FunctionID "
								+ functionId);
			else {
				execute(function, projectId);
			}
		} else
			execute(null, projectId);
	}

	public abstract void execute(Function function, String projectId);

	protected static class AddClientRequestsArtifacts extends FunctionCommand {
		private FunctionDTO functionDTO;

		public AddClientRequestsArtifacts(FunctionDTO functionDTO) {
			super(0L);
			this.functionDTO = functionDTO;
		}

		public void execute(Function function, String projectId) {

			Function funct = new Function(
										functionDTO,
										true,
										true,
										projectId);

			for( TestDTO test : functionDTO.tests ){
				TestCommand.create(test, funct.getId(), true, true);
			}
		}
	}

	protected static class Create extends FunctionCommand {
		private FunctionDTO functionDTO;
		private boolean isReadOnly;
		private boolean isApiArtifact;

		public Create(FunctionDTO functionDTO, boolean isApiArtifact, boolean isReadOnly) {
			super(0L);

			this.functionDTO   = functionDTO;
			this.isApiArtifact = isApiArtifact;
			this.isReadOnly	   = isReadOnly;
		}

		public void execute(Function function, String projectId) {
			new Function(functionDTO, isApiArtifact, isReadOnly, projectId);
		}
	}

	protected static class CreateRequestedFunction extends FunctionCommand {

		private FunctionDTO requestedFunctionDTO;
		private long requestingFunctionId;

		public CreateRequestedFunction(long requestingFunctionId, FunctionDTO requestedFunctionDTO) {
			super(0L);

			this.requestedFunctionDTO = requestedFunctionDTO;
			this.requestingFunctionId = requestingFunctionId;
		}

		public void execute(Function funct, String projectId) {

			requestedFunctionDTO.code = "{\n //#Mark this function as implemented by removing this line. \n return {};\n}";
			Function function = new Function(requestedFunctionDTO, false, false, projectId);

			function.addCaller(requestingFunctionId);

			FunctionCommand.addCallee(requestingFunctionId, function.getId());

			for( TestDTO test : requestedFunctionDTO.tests){
				TestCommand.create(test, function.getId(), false, false);
			}
		}
	}


