@Subclass(index=true)
public class ImplementBehavior extends Microtask
{
	public enum PromptType { WRITE, CALLEE_CHANGE, REMOVE_CALLEE, CORRECT };
	@Parent @Load private Ref<Function> function;
	private PromptType promptType;

	private long testId;					//Only defined for WRITE

	private int oldCalleeVersion;			//Only defined for CALLEE_CHANGED

	private String disputeText;				// Only defined for CORRECT and REMOVE_CALLEE
	private long calleeId;					// Only defined for REMOVE_CALLEE and CALLEE_CHANGED

	private long   disputeId;				//id of the artifact that disputed this function

	// Default constructor for deserialization
	private ImplementBehavior()
	{
	}

	// Initialization constructor for a WRITE write function. Microtask is not ready.
	public ImplementBehavior(Function function, long testId, String projectId)
	{
		super(projectId, function.getId());

		this.promptType = PromptType.WRITE;

		this.testId		= testId;

		implementBehavior(function, projectId);
	}

	// Initialization constructor for a DESCRIPTION_CHANGE write function. Microtask is not ready.
	public ImplementBehavior(Function function, long calleeId,
			int oldCalleeVersion, String projectId)
	{
		super(projectId,function.getId());
		this.promptType = PromptType.CALLEE_CHANGE;

		this.calleeId = calleeId;
		this.oldCalleeVersion = oldCalleeVersion;

		implementBehavior(function, projectId);
	}

	// Initialization constructor for a RE_EDIT write function. Microtask is not ready.
	public ImplementBehavior(Function function, String disputeText, long calleeId, String projectId)
	{
		super(projectId,function.getId());
		this.promptType = PromptType.REMOVE_CALLEE;

		this.disputeText = disputeText;
		this.calleeId = calleeId;
		implementBehavior(function, projectId);
	}
	public ImplementBehavior(Function function, String disputeText, String projectId)
	{
		super(projectId,function.getId());
		this.promptType = PromptType.CORRECT;

		this.disputeText = disputeText;

		implementBehavior(function, projectId);
	}


	public Microtask copy(String projectId)
	{
		switch (promptType) {
			case WRITE:
				return new ImplementBehavior( (Function) getOwningArtifact() , testId, projectId);
			case CALLEE_CHANGE:
				return new ImplementBehavior( (Function) getOwningArtifact() , calleeId, oldCalleeVersion, projectId);
			case REMOVE_CALLEE:
				return new ImplementBehavior( (Function) getOwningArtifact() , this.disputeText, disputeId, projectId);
			case CORRECT:
				return new ImplementBehavior((Function) getOwningArtifact(), this.disputeText, projectId);

			default:
				return null;
		}
	}

	public Key<Microtask> getKey()
	{
		return Key.create( function.getKey(), Microtask.class, this.id );
	}


	private void implementBehavior(Function function, String projectId)
	{
		this.function = function.getRef();
		ofy().load().ref(this.function);
		ofy().save().entity(this).now();
		FirebaseService.writeMicrotaskCreated(new ImplementBehaviorInFirebase(
				id,
				this.microtaskTitle(),
				this.microtaskName(),
				function.getName(),
				function.getId(),
				submitValue,
				function.getId(),
				this.promptType.name(),
				this.testId,
				this.calleeId,
				this.oldCalleeVersion,
				this.disputeText),
				Microtask.keyToString(this.getKey()),
				projectId);


		HistoryLog.Init(projectId).addEvent(new MicrotaskSpawned(this));
	}

	protected void doSubmitWork(DTO dto, String workerID)
	{
		function.get().implementBehaviorCompleted((ImplementBehaviorDTO) dto, disputeId , projectId);
//		WorkerCommand.awardPoints(workerID, this.submitValue);
		// increase the stats counter
		WorkerCommand.increaseStat(workerID, "functions",1);
	}

	public PromptType getPromptType()
	{
		return promptType;
	}

	protected Class getDTOClass()
	{
		return ImplementBehaviorDTO.class;
	}

		public Function getFunction()
	{
		return function.getValue();
	}


	public Artifact getOwningArtifact()
	{
		Artifact owning;
		try {
			return function.safe();
		} catch ( Exception e ){
			ofy().load().ref(this.function);
			return function.get();
		}
	}

	public String microtaskTitle()
	{
		return "Implement function behavior";
	}

	public String microtaskDescription()
	{
		return "implement function behaviorn";
	}


}

@Subclass(index=true)
public class DescribeFunctionBehavior extends Microtask
{
	public enum PromptType { WRITE, CORRECT, FUNCTION_CHANGED, CALLEE_CHANGED, ADT_CHANGED };

	@Parent @Load Ref<Function> functionRef;
	private Long functionId;
	private String functionName;

	private PromptType promptType;

	//Data for FUNCTION_CHANGED
	private int oldFunctionVersion;

	//Data for ADT_CHANGED
	private int oldADTVersion;
	private long ADTId;

	// Data for CORRECT
	private List<TestDisputedDTO> disputedTests;    // Description of the problem with the test case


	//Data for CALLEE_CHANGED
	private int oldCalleeVersion;
	private long calleeId;


	// Default constructor for deserialization
	private DescribeFunctionBehavior()
	{
	}

	// Constructor for WRITE Prompt Type for write a new behaviour and test of a function
	public DescribeFunctionBehavior(Ref<Function> Function, long functionId, String functionName, String projectId )
	{
		super(projectId,functionId);
		this.promptType	  = PromptType.WRITE;
		describeFunctionBehavior( Function, functionId, functionName );

	}

	// Constructor for FUNCTION_CHANGED Prompt Type for ask to edit the test code on a change of a function signature
	public DescribeFunctionBehavior(Ref<Function> Function, long functionId, String functionName, int oldFunctionVersion, String projectId)
	{
		super(projectId,functionId);
		this.promptType	  		= PromptType.FUNCTION_CHANGED;

		this.oldFunctionVersion = oldFunctionVersion;

		describeFunctionBehavior( Function, functionId, functionName );

	}
	// Constructor for ADT_CHANGED Prompt Type for ask to edit the test code on a change of a ADT Structure
	public DescribeFunctionBehavior(Ref<Function> Function, long functionId, String functionName, int oldADTVersion, long ADTId, String projectId)
	{
		super(projectId, functionId);
		this.promptType	  	= PromptType.ADT_CHANGED;

		this.oldADTVersion 	= oldADTVersion;
		this.ADTId			= ADTId;

		describeFunctionBehavior( Function, functionId, functionName );

	}
	// Constructor for CORRECT Prompt Type for ask to edit the test when has been issued
	public DescribeFunctionBehavior(Ref<Function> Function, long functionId, String functionName, List<TestDisputedDTO> disputedTests, String projectId)
	{
		super(projectId,functionId);
		this.promptType			= PromptType.CORRECT;

		this.disputedTests 	= disputedTests;

		describeFunctionBehavior( Function, functionId, functionName );
	}

	// Constructor for CALLEE CHANGED Promp
	public DescribeFunctionBehavior(Ref<Function> Function, long functionId, String functionName, long calleeId, int oldCalleeVersion, String projectId)
	{
		super(projectId,functionId);
		this.promptType			= PromptType.CALLEE_CHANGED;

		this.calleeId 			= calleeId;
		this.oldCalleeVersion	= oldCalleeVersion;
		describeFunctionBehavior( Function, functionId, functionName );
	}

	private void describeFunctionBehavior(Ref<Function> Function, long functionId, String functionName )
	{
		this.functionId   = functionId;
		this.functionName = functionName;
		this.functionRef = Function;

		ofy().save().entity(this).now();
		FirebaseService.writeMicrotaskCreated(
				new DescribeFunctionBehaviourInFirebase(
						id,
						this.microtaskTitle(),
						this.microtaskName(),
						functionName,
						functionId,
						submitValue,
						functionId,
						promptType.name(),
						oldFunctionVersion,
						oldADTVersion,
						ADTId,
						disputedTests,
						calleeId,
						oldCalleeVersion),
				Microtask.keyToString(this.getKey()),
				projectId);

		HistoryLog.Init(projectId).addEvent(new MicrotaskSpawned(this));

	}

	public Microtask copy(String projectId)
	{
		switch (promptType) {
			case WRITE:
				return new DescribeFunctionBehavior( this.functionRef, this.functionId, this.functionName, this.projectId);
			case FUNCTION_CHANGED:
				return new DescribeFunctionBehavior( this.functionRef, this.functionId, this.functionName, this.oldFunctionVersion, this.projectId);
			case ADT_CHANGED:
				return new DescribeFunctionBehavior( this.functionRef, this.functionId, this.functionName, this.oldADTVersion, this.ADTId, this.projectId);
			case CORRECT:
				return new DescribeFunctionBehavior( this.functionRef, this.functionId, this.functionName, this.disputedTests, this.projectId);
			default:

				return null;
		}
	}

	public Key<Microtask> getKey()
	{
		return Key.create( this.functionRef.getKey(), Microtask.class, this.id );
	}


	protected void doSubmitWork(DTO dto, String workerId)
	{
		Function function= functionRef.get();
		function.describeFunctionBehaviorCompleted((DescribeFunctionBehaviorDTO)dto);

		WorkerCommand.awardPoints(workerId, this.submitValue);
//		// increase the stats counter
		WorkerCommand.increaseStat(workerId, "describe_behavior",1);
	}
	public Artifact getOwningArtifact()
	{
		try {
			return functionRef.safe();
		} catch ( Exception e ){
			ofy().load().ref(this.functionRef);
			return functionRef.get();
		}
	}

	protected Class getDTOClass()
	{
		return DescribeFunctionBehaviorDTO.class;
	}

	public PromptType getPromptType()
	{
		return promptType;
	}

	public String microtaskTitle()
	{
		return "Describe Function Behavior";
	}

	public String microtaskDescription()
	{
		return "Describe Function Behavior";
	}
}

@Subclass(index=true)
public class ChallengeReview extends Microtask
{
	@Parent @Load private Ref<Artifact> artifact;
	private Key<Microtask> microtaskKeyUnderChallenge;
	private String challengeeWorkerId;
	private String challengerWorkerId;
	private String challengeText;
	private Key<Microtask> reviewKeyUnderChallenge;


	// Default constructor for deserialization
	private ChallengeReview()
	{
	}

	// Constructor for initial construction
	public ChallengeReview( String challengeText, String challengerWorkerId, String challengeeWorkerId, Key<Microtask> microtaskKeyUnderChallenge, Key<Microtask> reviewKeyUnderChallenge, long functionId, String projectId)
	{
		super(projectId,functionId);
		this.submitValue 				= 3;

		this.challengeText 				= challengeText;
		this.challengerWorkerId			= challengerWorkerId;
		this.challengeeWorkerId			= challengeeWorkerId;
		this.microtaskKeyUnderChallenge = microtaskKeyUnderChallenge;
		this.reviewKeyUnderChallenge	= reviewKeyUnderChallenge;

		Microtask microtaskUnderChallenge 	= ofy().load().key(microtaskKeyUnderChallenge).now();
		Microtask reviewUnderChallenge		= ofy().load().key(reviewKeyUnderChallenge).now();
		this.artifact = (Ref<Artifact>) Ref.create((Key<Artifact>) microtaskUnderChallenge.getOwningArtifact().getKey());
		System.out.println("micrtoask created");
		ofy().save().entity(this).now();

		FirebaseService.writeMicrotaskCreated(new ChallengeReviewInFirebase(
				id,
				this.microtaskTitle(),
				this.microtaskName(),
				this.artifact.get().getName(),
				this.artifact.get().getId(),
				functionId,
				submitValue,
				this.challengeText,
				microtaskKeyUnderChallenge
				),
				Microtask.keyToString(this.getKey()),
				projectId);

		// send feedback
		String udpatedData="{\"challengeStatus\": \"inProgress\", \"canBeChallenged\": false}";
    	FirebaseService.updateNewsfeed(
    			challengeeWorkerId,
    			udpatedData,
    			Microtask.keyToString(reviewKeyUnderChallenge),
    			projectId);
    	FirebaseService.updateNewsfeed(
    			challengerWorkerId,
    			udpatedData,
    			Microtask.keyToString(microtaskKeyUnderChallenge),
    			projectId);

		FirebaseService.writeWorkerNotification(
				new ChallengeNotificationInFirebase(
						"challenge.inProgress",
						Microtask.keyToString(reviewKeyUnderChallenge),
						microtaskUnderChallenge.microtaskName(),
						microtaskUnderChallenge.getOwningArtifact().getName()),
				challengeeWorkerId,
				projectId
		);

		HistoryLog.Init(projectId).addEvent(new MicrotaskSpawned(this));
	}

    public Microtask copy(String projectId,Long functionId)
    {
    	return new ChallengeReview( this.challengeText, this.challengerWorkerId, this.challengeeWorkerId, this.microtaskKeyUnderChallenge, this.reviewKeyUnderChallenge, this.functionId, this.projectId);
    }

	protected void doSubmitWork(DTO dto, String workerID)
	{
		System.out.println("submitting");

		ChallengeReviewDTO challengeDTO = (ChallengeReviewDTO) dto;
		Microtask microtaskUnderChallenge = ofy().load().key(microtaskKeyUnderChallenge).now();
		Microtask reviewUnderChallenge = ofy().load().key(reviewKeyUnderChallenge).now();

		String challengerResult = challengeDTO.isChallengeWon ? "won" : "lost";
		String challengeeResult = challengeDTO.isChallengeWon ? "lost" : "won";


		// send feedback
    	FirebaseService.postToNewsfeed(workerID, (
    		new NewsItemInFirebase(
        		this.submitValue,
    			this.submitValue,
    			this.microtaskName(),
				"Challenge Reviewed",
				Microtask.keyToString(this.getKey()),
				-1,
				"none",
				false)
	    	).json(),
	    	Microtask.keyToString(reviewKeyUnderChallenge),
    		projectId
	    );
    	String challengeeUpdatedData;
		String challengerUpdatedData;
    	if(challengeDTO.isChallengeWon){
			challengeeUpdatedData="{\"challengeStatus\": \"lost\", \"awardedPoints\": \"0\"}";
			challengerUpdatedData="{\"challengeStatus\": \"won\", \"awardedPoints\": \""+microtaskUnderChallenge.submitValue+"\"}";
    	}
    	else
    	{
    		challengeeUpdatedData="{\"challengeStatus\": \"won\", \"awardedPoints\": \""+reviewUnderChallenge.getSubmitValue()+"\"}";
			challengerUpdatedData="{\"challengeStatus\": \"lost\", \"awardedPoints\": \"0\"}";
    	}

    	FirebaseService.updateNewsfeed(
    			challengeeWorkerId,
    			challengeeUpdatedData,
    			Microtask.keyToString(reviewKeyUnderChallenge),
    			projectId);
    	FirebaseService.updateNewsfeed(
    			challengerWorkerId,
    			challengerUpdatedData,
    			Microtask.keyToString(microtaskKeyUnderChallenge),
    			projectId);


    	// send notification
		FirebaseService.writeWorkerNotification(
				new ChallengeNotificationInFirebase(
						"challenge."+challengeeResult,
						Microtask.keyToString(reviewKeyUnderChallenge),
						reviewUnderChallenge.microtaskName(),
						reviewUnderChallenge.getOwningArtifact().getName()),
				challengeeWorkerId,
				projectId
		);
		FirebaseService.writeWorkerNotification(
				 new ChallengeNotificationInFirebase(
							"challenge."+challengerResult,
							Microtask.keyToString(microtaskKeyUnderChallenge),
							microtaskUnderChallenge.microtaskName(),
							microtaskUnderChallenge.getOwningArtifact().getName()),
				challengerWorkerId,
				projectId
		);

		// increase the stats counter
		//WorkerCommand.increaseStat(workerID, "reviews",1);

	}

    public Key<Microtask> getKey()
	{
		return Key.create( artifact.getKey(), Microtask.class, this.id );
	}

	protected Class getDTOClass()
	{
		return ChallengeReviewDTO.class;
	}

	public Key<Microtask> getMicrotaskUnderChallengeKey()
	{
		return microtaskKeyUnderChallenge;
	}



	public Artifact getOwningArtifact()
	{
		Artifact owning;
		try {
			return artifact.safe();
		} catch ( Exception e ){
			ofy().load().ref(this.artifact);
			return artifact.get();
		}
	}

	public String microtaskTitle()
	{
		return "Challenge Review";
	}

	public String microtaskDescription()
	{
		return "challenge a submitted review";
	}

}

@Entity
public /*abstract*/ class Microtask
{
	static public String keyToString(Key<Microtask> key){
		String keyString = null;
		if( key != null )
			keyString = key.getParent().getId()+"-"+key.getId();
		return keyString;
	}

	static public Key<Microtask> stringToKey(String key){
		Key<Microtask> keyObj = null;
		if( !( key == null || key.length() == 0) ){
			String[] ids = key.split("-");
			Key<Artifact> parentKey = Key.create(Artifact.class, Long.parseLong(ids[0]) );
			keyObj = Key.create(parentKey,Microtask.class, Long.parseLong(ids[1]));
		}
		return keyObj;
	}

	static protected int DEFAULT_SUBMIT_VALUE = 10;

	@Id protected Long id;
	@Index String projectId;

	protected boolean assigned = false;
	protected boolean completed = false;
	protected boolean queued    = false;
	protected String reissuedFrom = "";
	protected int submitValue = DEFAULT_SUBMIT_VALUE;
	protected long assignmentTimeInMillis;	// time when worker is assigned microtask, in milliseconds
	protected String workerId;
	protected Long functionId;

	public String getWorkerId() {
		return workerId;
	}

	public Long getFunctionId() {
		return functionId;
	}

	public void setWorkerId(String workerId) {
		this.workerId = workerId;
		ofy().save().entity(this).now();
	}

	// Default constructor for deserialization
	protected Microtask()
	{
	}

	// Constructor for initialization. Microtask is set as ready.
	protected Microtask(String projectId, Long functionId)
	{
		this.workerId=null;
		this.projectId = projectId;
		this.projectId  = projectId;
		this.functionId = functionId;

	}

	// Creates a copy of this microtask, identical in all respects except with a new microtaskID
	// and with a reset completed and assignmentTime. The microtask is NOT queued onto the project work queue.
	// This method MUST be overridden in the subclass
	public Microtask copy(String projectId)
	{
		System.out.println("COPYING TASK "+this);
		throw new RuntimeException("Error - must implement in subclass!");
	}

	// Override this method to allow the microtask to decide, right before it is assigned,
	// if it is still needed
	protected boolean isStillNeeded(Project project) { return true; }

	public void submit(String jsonDTOData, String workerID,int awardedPoint)
	{
		// If this microtask has already been completed, drop it, and clear the worker from the microtask
		// TODO: move this check to the project, as this check will be too late for work creating review microtasks.
		if (this.completed){
			return;
		}

		try {
			DTO dto = DTO.read(jsonDTOData, getDTOClass());

			doSubmitWork(dto, workerID);

			this.completed = true;
			ofy().save().entity(this).now();

			// increase the stats counter
			WorkerCommand.increaseStat(workerID, "microtasks",1);
			WorkerCommand.awardPoints(workerID, awardedPoint);
			// write completed on firebase
			FirebaseService.writeMicrotaskCompleted( Microtask.keyToString(this.getKey()), workerID, projectId, this.completed);

		} catch( JsonParseException e) {
			e.printStackTrace();
		} catch( JsonMappingException e) {
			e.printStackTrace();
		} catch (IOException e) {
			e.printStackTrace();
		}


	}

	public void revise (String jsonDTOData, String excludedWorkerID,int awardedPoint, String reissueMotivation, String projectId)
	{
		// If this microtask has already been completed, drop it, and clear the worker from the microtask
		// TODO: move this check to the project, as this check will be too late for work creating review microtasks.
		if (this.completed){
			Logger.getLogger("LOGGER").severe("MICROTASK ALREADY COMPLETED: "+this.toString());
			return;
		}
		this.completed = true;
		ofy().save().entity(this).now();

		//copy the microtask
		Microtask newMicrotask = this.copy(projectId);
		Key<Microtask> a = ofy().save().entity(newMicrotask).now();
		String microtaskKey = Microtask.keyToString(newMicrotask.getKey());

		String reissuedFromMicrotaskKey = Microtask.keyToString(this.getKey());
		//enqueu the microtask
		ProjectCommand.queueMicrotask(newMicrotask.getKey(), excludedWorkerID);
		WorkerCommand.awardPoints( excludedWorkerID ,awardedPoint );

		//write the reissue field on the new microtask
		FirebaseService.writeMicrotaskReissuedFrom(microtaskKey, new ReissueInFirebase(reissuedFromMicrotaskKey, reissueMotivation), jsonDTOData,  projectId );
		// write completed on firebase
		FirebaseService.writeMicrotaskCompleted( Microtask.keyToString(this.getKey()), excludedWorkerID, projectId, this.completed);


	}

	public void skip(String workerID, boolean disablePoint, String projectId)
	{
		if(! disablePoint){
		// Increment the point value by 10
			this.submitValue *= 1.2;
		}
		this.workerId = "";
		ofy().save().entity(this).now();
		FirebaseService.writeMicrotaskPoints(Microtask.keyToString(this.getKey()), this.submitValue, projectId);
		HistoryLog.Init(projectId).addEvent(new MicrotaskSkipped(this, workerID));
	}


	public Key<Microtask> getKey()
	{
		throw new RuntimeException("Error - must implement in subclass!");
	}

	public long getID()
	{
		return id;
	}

	// returns the relative path to the UI for this microtask
	public String getUIURL() { return ""; }

	// This method MUST be overridden in the subclass to do submit work.
	protected void doSubmitWork(DTO dto, String workerID)
	{
		throw new RuntimeException("Error - must implement in subclass!");
	}

	// This method MUST be overridden in the subclass
	protected Class getDTOClass()
	{
		throw new RuntimeException("Error - must implement in subclass!");
	}

	// This method MUST be overridden in the subclass to provide the owning artifact.
	// The owning artifact is the artifact that will be modified by this microtask. If multiple artifacts
	// may be modified, the owning artifact is null.
	public Artifact getOwningArtifact()
	{
		throw new RuntimeException("Error - must implement in subclass!");
	}

	// This method MUST be overridden in the subclass to provide the name of the microtask.
	public String microtaskTitle()
	{
		throw new RuntimeException("Error - must implement in subclass!");
	}

	// This method MUST be overridden in the subclass to provide the name of the microtask.
	public String microtaskDescription()
	{
		throw new RuntimeException("Error - must implement in subclass!");
	}

	public String microtaskName()
	{
		// Get the name of the runtime microtask instance (e.g., ReuseSearch)
		return this.getClass().getSimpleName();
	}

	public long assignmentTimeInMillis()
	{
		return assignmentTimeInMillis;
	}
	public void setReissuedFrom(String microtaskKey)
	{
		this.reissuedFrom=microtaskKey;
	}
	public int getSubmitValue()
	{
		return submitValue;
	}

	// Should only be called from within the entity group of the owning artifact
	public static LoadResult<Microtask> find(Key<Microtask> microtaskKey)
	{
		return (LoadResult<Microtask>) ofy().load().key(microtaskKey);
	}

	public String toJSON(){
		return toJSON(new JSONObject());
	}

	public String toJSON(JSONObject json){
		try {
			json.put("key", Microtask.keyToString(this.getKey()));
			json.put("id", this.id);
			json.put("type", this.microtaskName());
			json.put("description", this.microtaskDescription());
			json.put("title", this.microtaskTitle());
			json.put("submitValue", this.getSubmitValue());
		} catch (JSONException e) {
			// TODO Auto-generated catch block
			e.printStackTrace();
		}
		return json.toString();
	}

	public boolean isAssignedTo(String workerId){
		if(this.getWorkerId()!=null && this.getWorkerId().isEmpty())
			return true;

		boolean isAssigned= this.getWorkerId() != null && this.getWorkerId().equals( workerId );

		return isAssigned;
	}
}
